import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const App = ({ className }) => {
   const [state, setState] = useState({
      name: '',
      email: '',
      phone: '',
      gender: '',
      data: []
   });

   const onEditData = async (e) => {
      let datum = state.data;

      let objIndex = datum.findIndex((e => e.id == 1));
   }

   const onDeleteData = async (e) => {

      let datum = state?.data;
      datum.splice(e, 1);

      setState({
         ...state,
         data: datum
      })
   }

   const onSubmitForm = async (e) => {
      e.preventDefault();

      const {
         name,
         email,
         phone,
         gender,
         data
      } = state
      const isEmailExist = data.filter(val => val == email);
      console.log('isEmailExist', isEmailExist);

      // Input validation alert :
      if (
         !name ||
         !email ||
         !phone ||
         !gender
      ) {
         alert('All input must be filled !');
      } else {

         const payload = {
            name,
            email,
            phone,
            gender
         }

         const joined = state.data.concat(payload);
         setState({
            data: joined,
            name: '',
            email: '',
            phone: '',
            gender: ''
         })
      }

      // Email validation alert :
      // alert('Email is already used !');
   };

   const onResetForm = (e) => {
      e.preventDefault();
      // console.log('onReset', e);

      setState({
         ...state,
         name: '',
         email: '',
         phone: '',
         gender: '',
      });
   };

   useEffect(() => {
      // console.log('state.name', state)
      console.log('state.data', state.data)
   })

   const _handlerInput = (fieldType, text) => {
      // console.log('name', nameUser.target.value)
      const isFieldName = fieldType == "nameUser";
      const isFieldEmail = fieldType == "emailUser";
      const isFieldPhone = fieldType == "phoneUser";
      const isFieldGender = fieldType == "genderUser";

      if (isFieldName) {
         setState({ ...state, name: text.target.value })
      } else if (isFieldEmail) {
         setState({ ...state, email: text.target.value })
      } else if (isFieldPhone) {
         setState({ ...state, phone: text.target.value })
      } else if (isFieldGender) {
         setState({ ...state, gender: text.target.value })
      } else {
         alert('All input must be filled !');
      }
   }

   return <>
      <div className={className}>
         <h1>Users</h1>
         <form onSubmit={onSubmitForm} onReset={onResetForm} onEdit>
            <div className="form-group">
               <label>Name :</label>
               <input
                  aria-label="name-input"
                  type="text"
                  placeholder="User Example"
                  className="form-control"
                  value={state.name}
                  onChange={(text) => { _handlerInput('nameUser', text) }}
               />
            </div>
            <div className="form-group">
               <label>Email :</label>
               <input
                  aria-label="email-input"
                  type="email"
                  placeholder="username@example.com"
                  className="form-control"
                  value={state.email}
                  onChange={(text) => { _handlerInput('emailUser', text) }}
               />
            </div>
            <div className="form-group">
               <label>Phone :</label>
               <input
                  aria-label="phone-input"
                  type="tel"
                  placeholder="081123123123"
                  className="form-control"
                  value={state.phone}
                  onChange={(text) => { _handlerInput('phoneUser', text) }}
               />
            </div>
            <p>Gender : </p>
            <select
               aria-label="gender-input"
               className="form-control"
               value={state.gender}
               onChange={(text) => { _handlerInput('genderUser', text) }}
            >
               <option value="">Pick a gender</option>
               <option value="male">Male</option>
               <option value="female">Female</option>
            </select>
            <div className='button-group'>
               <button className='btn btn-secondary' type='reset'>Clear</button>
               <button className='btn btn-secondary' type='submit'>Create</button>
            </div>
         </form>
         <br />
         <table aria-label="table-output">
            <thead>
               <tr>
                  <th>ID</th>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Phone</th>
                  <th>Gender</th>
                  <th>Actions</th>
               </tr>
            </thead>
            <tbody>
               {
                  state?.data && state?.data?.map((value, index) => {
                     return (
                        <tr key={index}>
                           <td>{index + 1}</td>
                           <td>{state.data[index].name}</td>
                           <td>{state.data[index].email}</td>
                           <td>{state.data[index].phone}</td>
                           <td>{state.data[index].gender}</td>
                           <td className='action-buttons'>
                              <button className='btn btn-success btn-edit'>Edit</button>
                              <button className='btn btn-success btn-edit' onClick={() => { onEditData(index) }}>Edit</button>
                              {/*<button className='btn btn-danger btn-delete'>Delete</button>*/}
                              <button className='btn btn-danger btn-delete' onClick={() => { onDeleteData(index) }}>Delete</button>
                           </td>
                        </tr>
                     );
                  })
               }
            </tbody>
         </table>
      </div>
   </>;
};

export default styled(App)`
   margin: 100px auto;
   width: 470px;

   input, select {
     margin-bottom: 10px;
     display: block;
     width: 100%;
   } 

   table, th, td {
     border: 1px solid black;
   }

   table {
     width: calc(100%);
     border-collapse: collapse;
   }

   td:not(:first-child) {
     max-width: 100px;
     overflow: hidden;
     text-overflow: ellipsis;
     white-space: nowrap;
   }

   p {
     margin-bottom: 2px;
   }

   .button-group {
     display: flex;
     justify-content: space-between;
     margin-top: 25px;
     button {
       width: 100px;
       height: 35px;
       cursor: pointer;
     }
   }

   select:invalid {
     color: #666;
   }
   select.grey {
     color: grey;
   }
   option[value=""][disabled] {
     color: grey;
   }
   option {
     color: #000;
   }

   td.action-buttons {
     text-align: center;
     padding: 3px 0px;
     max-width: initial;
     button {
       cursor: pointer;
       margin: 0px 3px;
     }
   }
 `;
